import styled from 'styled-components';

const Box = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 400px;
    height: 400px;
    border: 1px solid #000;
    text-align: center;
    padding: 50px;
    font-size: 16px;
`;

const IptGroup = styled.div`
    width: 100%;
    margin-bottom: 50px;
`;

const Label = styled.div`
    width: 100px;
    display: inline-block;
    text-align: left;
`;

const Input = styled.input`
    width: 280px;
    height: 50px;
    border: 1px solid #000;
`;

const Button = styled.button`
    width: 100%;
    height: 50px;
    cursor: pointer;
    border: 1px solid #0f4c81;
    background-color: #0f4c81;
    font-size: 16px;
    margin-top: 30px;
    color: #fff;
    &:hover {
        transform: scale(1.05);
    }
    ${(props) =>
        props.color === 'white' &&
        `
        background-color: #fff;
        color: #0f4c81;
    `}
`;

const Box2 = (props) => {
    return (
        <Box>
            <IptGroup>
                <Label>아이디</Label>
                <Input></Input>
            </IptGroup>
            <IptGroup>
                <Label>비밀번호</Label>
                <Input></Input>
            </IptGroup>
            <Button>로그인</Button>
            <Button color="white">회원가입</Button>
        </Box>
    );
};

export default Box2;
