import Router from "next/router";
import { useState } from "react";
import "../static/styles.scss";
import axios from "axios";

const SignUp = () => {
  const [userInfo, setUserInfo] = useState({
    id: "",
    password: "",
    name: "",
    email: "",
    sex: "",
    age: "",
    errors: {
      id: "",
      password: "",
      name: "",
      email: "",
    },
  });

  const handleChangeInput = (name, value) => {
    setUserInfo({
      ...userInfo,
      [name]: value,
    });
  };

  const validateInput = (name, value) => {
    let idRegx = /^[a-z]+[a-z0-9]{5,19}$/g;
    let emailRegx = /^(([^<>()\[\].,;:\s@"]+(\.[^<>()\[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/i;
    let nameRegx = /^[가-힣]{1,4}$/; // 한글 이름

    switch (name) {
      case "id":
        errors.id = idRegx.test(value)
          ? ""
          : "Id must start with english letters and numbers and from 6 letters to 20 letters";
        break;

      case "password":
        errors.password =
          value.length < 8 ? "Password must be 8 characters long!" : "";
        break;

      case "name":
        erros.name = nameRex.test(value)
          ? ""
          : "Name must be Korean letter and from 2 letters to 5 letters";
        break;

      case "email":
        errors.email = emailRex.test(value) ? "" : "Email is not valid!";
        break;
      default:
        break;
    }
  };

  const handleClick = (e) => {
    setUserInfo({
      ...userInfo,
      sex: e.target.value,
    });
  };

  const handleSubmit = () => {
    saveUserInfo(userInfo);
  };

  const saveUserInfo = (data) => {
    axios({
      url: "https://tutorial.team-everywhere.com/api/user",
      method: "post",
      data: data,
    })
      .then((res) => {
        console.log(res);
        localStorage.setItem("userInfo", JSON.stringify(res.data.result));
        console.log("Sign up Complete");
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className="wrapper signup-wrapper">
      <div className="form signup-form">
        <header>
          <h2 className="title">Sign up</h2>
          <p className="subtitle">
            Don't have an account? Let's go ahead and get you signed up.
          </p>
        </header>
        <main>
          <form className="signup-information">
            {/* id input */}
            <div className="field">
              <h2 className="inputTitle">ID</h2>
              <input
                required
                type="text"
                className="signup-input"
                id="idInput"
                name="id"
                onChange={(e) => {
                  handleChangeInput(e.target.name, e.target.value);
                  validateInput(e.target.name, e.target.value);
                }}
                noValidate
              />
              <label htmlFor="idInput">ID</label>
            </div>

            {/* password input */}
            <div className="field">
              <h2 className="inputTitle">Password</h2>
              <input
                required
                type="password"
                className="signup-input"
                id="pwdInput"
                name="password"
                onChange={(e) => {
                  handleChangeInput(e.target.name, e.target.value);
                  validateInput(e.target.name, e.target.value);
                }}
                noValidate
              />
              <label htmlFor="pwdInput">******</label>
            </div>

            {/* name input */}
            <div className="field">
              <h2 className="inputTitle">Username</h2>
              <input
                required
                type="text"
                className="signup-input"
                id="nameInput"
                name="name"
                onChange={(e) => {
                  handleChangeInput(e.target.name, e.target.value);
                  validateInput(e.target.name, e.target.value);
                }}
                noValidate
              />
              <label htmlFor="nameInput">Username</label>
            </div>

            {/* email input */}
            <div className="field">
              <h2 className="inputTitle">Email</h2>
              <input
                required
                type="email"
                className="signup-input"
                id="emailInput"
                name="email"
                onChange={(e) => {
                  handleChangeInput(e.target.name, e.target.value);
                }}
                noValidate
              />
              <label htmlFor="emailInput">email@example.com</label>
            </div>

            {/* gender input */}
            <div className="field">
              <h2 className="inputTitle">Gender</h2>
              <span className="gender__male">
                <input
                  type="radio"
                  name="sex"
                  value="0"
                  onClick={handleClick}
                />{" "}
                Male
              </span>
              <span className="gender__female">
                <input
                  type="radio"
                  name="sex"
                  value="1"
                  onClick={handleClick}
                />{" "}
                Female
              </span>
            </div>

            {/* age input */}
            <div className="field">
              <h2 className="inputTitle">Age</h2>
              <input
                required
                type="number"
                className="signup-input"
                id="ageInput"
                name="age"
                onChange={(e) => {
                  handleChangeInput(e.target.name, e.target.value);
                }}
              />
              <label htmlFor="ageInput">AGE</label>
            </div>
          </form>
        </main>
        <footer>
          <div className="field">
            <input type="checkbox" name="terms" />
            <label>I agree to the Terms and Conditions</label>
          </div>
          <div className="actions">
            <input
              type="button"
              name="commit"
              value="Sign up"
              className="btn signup-btn"
              onClick={() => {
                Router.push("/signin");
                handleSubmit();
              }}
            />
          </div>
        </footer>
      </div>
    </div>
  );
};

export default SignUp;
