import Router from "next/router";
import { useState } from "react";
import "../static/styles.scss";
import axios from "axios";

const SignUp = () => {
  const [userInfo, setUserInfo] = useState({
    id: "",
    isIdValid: false,
    password: "",
    isPassWordValid: false,
    username: "",
    isNameValid: false,
    email: "",
    isEmailValid: false,
    sex: "",
    age: "",
  });

  const handleChangeInput = (name, value) => {
    setUserInfo({
      ...userInfo,
      [name]: value,
    });
  };

  const handleClick = (e) => {
    setUserInfo({
      ...userInfo,
      sex: e.target.value,
    });
  };

  const handleSubmit = () => {
    saveUserInfo(userInfo);
  };

  const saveUserInfo = (data) => {
    axios({
      url: "https://tutorial.team-everywhere.com/api/user",
      method: "post",
      data: data,
    })
      .then((res) => {
        console.log(res);
        localStorage.setItem("userInfo", JSON.stringify(res.data.result));
        console.log("Sign up Complete");
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className="wrapper signup-wrapper">
      <div className="form signup-form">
        <header>
          <h2 className="title">Sign up</h2>
          <p className="subtitle">
            Don't have an account? Let's go ahead and get you signed up.
          </p>
        </header>
        <main>
          <form className="signup-information">
            {/* id input */}
            <div className="field">
              <label htmlFor="idInput">ID</label>
              <input
                type="text"
                className="signup-input"
                placeholder="ID"
                id="idInput"
                name="id"
                onChange={(e) => {
                  handleChange(e.target.name, e.target.value);
                }}
                required={true}
              />
            </div>

            {/* password input */}
            <div className="field">
              <label htmlFor="pwdInput">Password</label>
              <input
                type="password"
                className="signup-input"
                placeholder="******"
                id="pwdInput"
                name="password"
                onChange={(e) => {
                  handleChange(e.target.name, e.target.value);
                }}
                required={true}
              />
            </div>

            {/* name input */}
            <div className="field">
              <label htmlFor="nameInput">Username</label>
              <input
                type="text"
                className="signup-input"
                placeholder="username"
                id="nameInput"
                name="username"
                onChange={(e) => {
                  handleChange(e.target.name, e.target.value);
                }}
                required={true}
              />
            </div>

            {/* email input */}
            <div className="field">
              <label htmlFor="emailInput">Email</label>
              <input
                type="email"
                className="signup-input"
                placeholder="email@example.com"
                id="emailInput"
                name="email"
                onChange={(e) => {
                  handleChange(e.target.name, e.target.value);
                }}
                required={true}
              />
            </div>

            {/* gender input */}
            <div className="field">
              <label htmlFor="gender">Gender</label>
              <span className="gender__male">
                <input
                  type="radio"
                  name="sex"
                  value="0"
                  onClick={handleClick}
                />{" "}
                Male
              </span>
              <span className="gender__female">
                <input
                  type="radio"
                  name="sex"
                  value="1"
                  onClick={handleClick}
                />{" "}
                Female
              </span>
            </div>

            {/* age input */}
            <div className="field">
              <label htmlFor="ageInput">AGE</label>
              <input
                type="number"
                className="signup-input"
                id="ageInput"
                onChange={handleChange}
                required={true}
              />
            </div>
          </form>
        </main>
        <footer>
          <div className="field">
            <input type="checkbox" name="terms" />
            <label>I agree to the Terms and Conditions</label>
          </div>
          <div className="actions">
            <input
              type="button"
              name="commit"
              value="Sign up"
              className="btn signup-btn"
              onClick={() => {
                Router.push("/signin");
                handleSubmit();
              }}
            />
          </div>
        </footer>
      </div>
    </div>
  );
};

export default SignUp;
