import Router from "next/router";
import { useState } from "react";
import "../static/styles.scss";
import Input from "../components/form/input.js";

const SignUp = () => {
  const [userInfo, setUserInfo] = useState({
    id: "",
    password: "",
    username: "",
    email: "",
    sex: "",
    age: "",
  });

  const handleChangeInput = (name, value) => {
    setUserInfo({
      ...userInfo,
      [name]: value,
    });
  };

  const handleClick = (e) => {
    setUserInfo({
      ...userInfo,
      sex: e.target.value,
    });
  };

  return (
    <div className="wrapper signup-wrapper">
      <div className="form signup-form">
        <header>
          <h2 className="title">Sign up</h2>
          <p className="subtitle">
            Don't have an account? Let's go ahead and get you signed up.
          </p>
        </header>
        <main>
          <form className="signup-information">
            <Input
              type="text"
              placeholder="ID"
              label="ID"
              name="id"
              autoComplete="off"
              autoFocus={true}
              onChange={handleChangeInput}
            />
            <Input
              type="password"
              placeholder="******"
              label="Password"
              name="password"
              autoComplete="off"
              onChange={handleChangeInput}
            />
            <Input
              type="text"
              placeholder="Username"
              label="Username"
              name="username"
              autoComplete="off"
              onChange={handleChangeInput}
              required={true}
            />
            <Input
              type="email"
              placeholder="email@example.com"
              label="Email"
              name="email"
              onChange={handleChangeInput}
              required={true}
            />
            <div className="field">
              <label htmlFor="gender">Gender</label>
              <span className="gender__male">
                <input
                  type="radio"
                  name="sex"
                  value="male"
                  onClick={handleClick}
                />{" "}
                Male
              </span>
              <span className="gender__female">
                <input
                  type="radio"
                  name="sex"
                  value="female"
                  onClick={handleClick}
                />{" "}
                Female
              </span>
            </div>
            <Input
              type="number"
              label="Age"
              name="age"
              onChange={handleChangeInput}
              required={true}
            />
          </form>
        </main>
        <footer>
          <div className="field">
            <input type="checkbox" name="terms" />
            <label>I agree to the Terms and Conditions</label>
          </div>
          <div className="actions">
            <input
              type="button"
              name="commit"
              value="Sign up"
              className="btn"
              onClick={() => {
                Router.push("/signin");
                console.log(userInfo);
              }}
            />
          </div>
        </footer>
      </div>
    </div>
  );
};

export default SignUp;
